    public void cek(){
     Connection kon = (Connection) konek.getConnection();
    try{
        Statement statement = kon.createStatement();
            String sql = "SELECT * FROM login WHERE username = '"+tuser.getText()+"' and password ='"+tpass.getText()+"'";
            ResultSet kr = statement.executeQuery(sql);
            
            if (kr.next()){
                if(kr.getString("level").equals("pelayan")){
                    makanan mu = new makanan();
                    mu.show();
                    this.dispose();
                }else if(kr.getString("level").equals("kasir")){
                    pesanan mu = new pesanan();
                    mu.show();
                    this.dispose();
                }
            }else{
                JOptionPane.showMessageDialog(null, "Maaf Password Atau Username anda salah");
                tuser.setText("");
                tpass.setText("");
                tuser.requestFocus();
                
            }
    }catch (Exception e){
        JOptionPane.showMessageDialog(null, e);
    }
    }
    
