    private void datatable(){
        DefaultTableModel tbl= new DefaultTableModel();
        tbl.addColumn("ID MAKANAN");
        tbl.addColumn("NAMA MAKANAN");
        tbl.addColumn("HARGA MAKANAN");
        tbl.addColumn("STATUS MAKANAN");
        
        tabel.setModel(tbl);
        try{
            Statement statement = (Statement) konek.getConnection().createStatement();
            ResultSet res = statement.executeQuery("SELECT * FROM makanan");
            while (res.next())
            {
                tbl.addRow(new Object[]{
                res.getString("idm"),
                res.getString("nama"),
                res.getString("harga"),
                res.getString("status"),
                });
            tabel.setModel(tbl);
            }
        }catch (Exception e){
            JOptionPane.showMessageDialog(rootPane, "Gagal");
            
        }
        
    }

