    private void datatable(){
        DefaultTableModel tbl= new DefaultTableModel();
        tbl.addColumn("NO PESANAN");
        tbl.addColumn("NO MEJA PELANGGAN");
        tbl.addColumn("MAKANAN");
        tbl.addColumn("MINUMAN");
        
        tabel.setModel(tbl);
        try{
            Statement statement = (Statement) konek.getConnection().createStatement();
            ResultSet res = statement.executeQuery("SELECT * FROM pesanan");
            while (res.next())
            {
                tbl.addRow(new Object[]{
                res.getString("nopesanan"),
                res.getString("nomeja"),
                res.getString("makanan"),
                res.getString("minuman"),
                });
            tabel.setModel(tbl);
            }
        }catch (Exception e){
            JOptionPane.showMessageDialog(rootPane, "Gagal");
            
        }
        
    }

