    private void bcreateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bcreateActionPerformed
      String nopesanan = tnopes.getText();
      String nomeja = tmeja.getText();
      String makanan = tmakanan.getText();
      String minuman = tminuman.getText();
        
        try{
            Statement statement = (Statement)konek.getConnection().createStatement();
            statement.executeUpdate("INSERT INTO pesanan VALUES ('"+nopesanan+"','"+nomeja+"','"+makanan+"','"+minuman+"')");
            statement.close();
            JOptionPane.showMessageDialog(null, "Data Berhasil dibuat");
        }catch (Exception t){
            JOptionPane.showMessageDialog(null, "Data Gagal dibuat");
        }
        datatable(); 
    }//GEN-LAST:event_bcreateActionPerformed

