    private void bupdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bupdateActionPerformed
        if (bupdate.getText() == "bupdate"){
            tnopes.requestFocus();
        }else{
        String nopesanan = tnopes.getText();
        String nomeja = tmeja.getText();
        String makanan = tmakanan.getText();
        String minuman = tminuman.getText();
            
            try{
                com.mysql.jdbc.Statement statement = (com.mysql.jdbc.Statement)konek.getConnection().createStatement();
                statement.executeUpdate("UPDATE pesanan SET nopesanan = '"+nopesanan+"',nomeja= '"+nomeja+"',makanan='"+makanan+"',"+"minuman='"+minuman+"'"+ "WHERE nopesanan = '"+nopesanan+"' OR nomeja='"+nomeja+"'");
                statement.close();
                JOptionPane.showMessageDialog(null, "Data Berhasil Diubah");
            }catch (Exception t){
                JOptionPane.showMessageDialog(null, "Data Gagal Diubah");
            }
            datatable();
       }// TODO add your handling code here:
    }//GEN-LAST:event_bupdateActionPerformed

    private void bdeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bdeleteActionPerformed
       String nopesanan = tnopes.getText();
        String nomeja = tmeja.getText();
        try{
            com.mysql.jdbc.Statement statement = (com.mysql.jdbc.Statement)konek.getConnection().createStatement();
            statement.executeUpdate("DELETE FROM pesanan WHERE nopesanan='"+nopesanan+"' OR nomeja='"+nomeja+"';");
            JOptionPane.showMessageDialog(null, "Data Berhasil di hapus");
            tnopes.setText("");
            tmeja.setText("");
            tmakanan.setText("");
            tminuman.setText("");
       
            
        }catch (Exception t){
            JOptionPane.showMessageDialog(null, "Data Gagal dihapus");
        }
        datatable();
    }//GEN-LAST:event_bdeleteActionPerformed

    private void tabelMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelMouseClicked
        int i = tabel.getSelectedRow();
        if (i == -1){
            return;
        }
        String nopesanan = (String) tabel.getValueAt(i, 0);
        tnopes.setText(nopesanan);
        String nomeja = (String) tabel.getValueAt(i, 1);
        tmeja.setText(nomeja);
        String makanan = (String) tabel.getValueAt(i, 2);
        tmakanan.setText(makanan);
        String minuman = (String) tabel.getValueAt(i, 3);
        tminuman.setText(minuman);
    }//GEN-LAST:event_tabelMouseClicked

